	public static function getParagraph() {
		$nParagraph = rand(0, count(self::$aLorem) - 1);
		return self::$aLorem[$nParagraph];
	}
    public static function getSentence() {
    	$sParagraph = self::getParagraph();
    	$aSentences = explode('.', $sParagraph);
        $nSentence = rand(0, count($aSentences) - 2);
        return $aSentences[$nSentence].'.';
    }
    public static function getWord($bCapitalize=true) {
    	$sSentence = self::getSentence();
    	$aWords = preg_split('/[\s]+/', $sSentence);
    	$nWord = preg_replace('/[^a-z0-9]/i', '', rand(1, count($aWords) - 2));
    	if ($bCapitalize) {
    	    return ucfirst(strtolower($aWords[$nWord]));
	    } else {
	        return strtolower($aWords[$nWord]);
        }
    }
